            transform: translateY(0);
        }

        .site-notice .site-notice__actions {
            display: flex;
            gap: 0.5rem;
            flex-wrap: wrap;
            justify-content: flex-end;
            margin-left: auto;
        }

        [dir="rtl"] .site-notice .site-notice__actions {
            margin-left: 0;
            margin-right: auto;
        }

        .site-notice .site-notice__actions button {
