            }

            [dir="rtl"] .site-notice .site-notice__actions {
                margin-right: 0;
            }

            .site-notice .site-notice__actions button {
                flex: none;
                min-width: 70px;
                max-width: 100px;
                padding: 6px 10px;
                font-size: 11px;
                white-space: nowrap;
                overflow: hidden;
                text-overflow: ellipsis;
                border-radius: 3px;
            }
        }
    </style>
