    <script>
        (function () {
            function initPasswordToggles() {
                const eyeSvg = `
            <svg class="icon svg-icon-ti-ti-eye" xmlns="http://www.w3.org/2000/svg" width="24" height="24"
                viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2"
                stroke-linecap="round" stroke-linejoin="round">
                <path d="M10 12a2 2 0 1 0 4 0a2 2 0 0 0 -4 0"></path>
                <path d="M21 12c-2.4 4 -5.4 6 -9 6c-3.6 0 -6.6 -2 -9 -6c2.4 -4 5.4 -6 9 -6c3.6 0 6.6 2 9 6"></path>
            </svg>`;

                const eyeOffSvg = `
            <svg class="icon svg-icon-ti-ti-eye-off" xmlns="http://www.w3.org/2000/svg" width="24" height="24"
                viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2"
                stroke-linecap="round" stroke-linejoin="round">
                <path d="M10.585 10.587a2 2 0 0 0 2.829 2.828" />
                <path d="M16.681 16.673a8.717 8.717 0 0 1 -4.681 1.327c-3.6 0 -6.6 -2 -9 -6c1.272 -2.12 2.712 -3.678 4.32 -4.674m2.86 -1.146a9.055 9.055 0 0 1 1.82 -.18c3.6 0 6.6 2 9 6c-.666 1.11 -1.379 2.067 -2.138 2.87" />
                <path d="M3 3l18 18" />
            </svg>`;

                document.querySelectorAll("[data-bb-toggle-password]").forEach(btn => {
                    btn.addEventListener("click", function (e) {
                        e.preventDefault();

                        const wrapper = this.closest(".input-group");
                        const input = wrapper.querySelector("[data-bb-password]");

                        const isHidden = input.type === "password";
                        input.type = isHidden ? "text" : "password";
                        this.innerHTML = isHidden ? eyeOffSvg : eyeSvg;
                    });
                });
            }

            document.readyState === "loading"
                ? document.addEventListener("DOMContentLoaded", initPasswordToggles)
                : initPasswordToggles();
        })();

    </script>
